        onDepartmentAdded={() => {
          loadDepartments();
          loadDashboardStats();
        }}
      />

      <AddClasseModal 
        isOpen={showAddClasseModal}
        onClose={() => setShowAddClasseModal(false)}
        onClasseAdded={() => {
          loadClasses();
          loadDashboardStats();
        }}
      />
    </div>
  );
};

export default AdminDashboard;
