import React, { useState, useEffect } from 'react';
import { LayoutDashboard, Users, BookOpen, Building2, UserCheck, FileText, Bell, Menu, X, TrendingUp, AlertCircle, Plus, Edit, Trash2, Search, Loader, LogOut, User } from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { departementService, enseignantService, etudiantService, classeService } from '../services/adminServices';
import AddStudentModal from './AddStudentModal';
import AddTeacherModal from './AddTeacherModal';
import AddDepartmentModal from './AddDepartmentModal';
import AddClasseModal from './AddClasseModal';

const AdminDashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [activeMenu, setActiveMenu] = useState('dashboard');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Ã‰tats pour les modaux
  const [showAddStudentModal, setShowAddStudentModal] = useState(false);
  const [showAddTeacherModal, setShowAddTeacherModal] = useState(false);
  const [showAddDepartmentModal, setShowAddDepartmentModal] = useState(false);
  const [showAddClasseModal, setShowAddClasseModal] = useState(false);

  // Ã‰tats pour les donnÃ©es
  const [stats, setStats] = useState({
    students: 0,
    teachers: 0,
    departments: 0,
    classes: 0
  });
  const [studentsData, setStudentsData] = useState([]);
  const [teachersData, setTeachersData] = useState([]);
  const [departmentsData, setDepartmentsData] = useState([]);
  const [classesData, setClassesData] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  // Ã‰tats pour le profil
  const [isEditingProfile, setIsEditingProfile] = useState(false);
  const [profileData, setProfileData] = useState({
    nom: user?.nom || '',
    prenom: user?.prenom || '',
    email: user?.email || '',
    telephone: user?.telephone || '',
    role: user?.role || 'Administrateur'
  });

  // Chargement des statistiques au dÃ©marrage
  useEffect(() => {
    loadDashboardStats();
  }, []);

  // Chargement des donnÃ©es selon le menu actif
  useEffect(() => {
    switch (activeMenu) {
      case 'students':
        loadStudents();
        break;
      case 'teachers':
        loadTeachers();
        break;
      case 'departments':
        loadDepartments();
        break;
      case 'subjects':
        loadClasses();
        break;
      default:
        break;
    }
  }, [activeMenu]);

  // Fonction pour charger les statistiques
  const loadDashboardStats = async () => {
    try {
      setLoading(true);
      setError(null);
      
      // Charger les donnÃ©es directement
      const [deptsData, ensData, etuData, classesData] = await Promise.all([
        departementService.getAll().catch(() => []),
        enseignantService.getAll().catch(() => ({ success: false, data: [] })),
        etudiantService.getAll().catch(() => ({ success: false, data: [] })),
        classeService.getAll().catch(() => [])
      ]);

      // GÃ©rer le format { value: [...] } ou tableau direct
      const deptsArray = Array.isArray(deptsData) ? deptsData : (deptsData.value || deptsData.data || []);
      const ensArray = ensData.success ? (ensData.data || []) : (Array.isArray(ensData) ? ensData : (ensData.value || []));
      const etuArray = etuData.success ? (etuData.data || []) : (Array.isArray(etuData) ? etuData : (etuData.value || []));
      const classesArray = Array.isArray(classesData) ? classesData : (classesData.value || classesData.data || []);

      setStats({
        students: etuArray.length || 0,
        teachers: ensArray.length || 0,
        departments: deptsArray.length || 0,
        classes: classesArray.length || 0
      });
      
      console.log('ðŸ“Š Statistiques chargÃ©es:', {
        students: etuArray.length,
        teachers: ensArray.length,
        departments: deptsArray.length,
        classes: classesArray.length
      });
    } catch (err) {
      console.error('âŒ Erreur chargement stats:', err);
      setError('Erreur lors du chargement des statistiques');
    } finally {
      setLoading(false);
    }
  };

  // Fonction pour charger les Ã©tudiants
  const loadStudents = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('ðŸ”„ Chargement des Ã©tudiants...');
      const result = await etudiantService.getAll();
      console.log('ðŸ“¥ RÃ©sultat Ã©tudiants:', result);
      if (result.success) {
        setStudentsData(result.data || []);
        console.log('âœ… Ã‰tudiants chargÃ©s:', result.data?.length || 0);
      } else {
        setError(result.message);
        console.error('âŒ Erreur:', result.message);
      }
    } catch (err) {
      console.error('âŒ Erreur chargement Ã©tudiants:', err);
      setError('Erreur lors du chargement des Ã©tudiants');
    } finally {
      setLoading(false);
    }
  };

  // Fonction pour charger les enseignants
  const loadTeachers = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('ðŸ”„ Chargement des enseignants...');
      const result = await enseignantService.getAll();
      console.log('ðŸ“¥ RÃ©sultat enseignants:', result);
      if (result.success) {
        setTeachersData(result.data || []);
        console.log('âœ… Enseignants chargÃ©s:', result.data?.length || 0);
      } else {
        setError(result.message);
        console.error('âŒ Erreur:', result.message);
      }
    } catch (err) {
      console.error('âŒ Erreur chargement enseignants:', err);
      setError('Erreur lors du chargement des enseignants');
    } finally {
      setLoading(false);
    }
  };

  // Fonction pour charger les dÃ©partements
  const loadDepartments = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('ðŸ”„ Chargement des dÃ©partements...');
      const data = await departementService.getAll();
      console.log('ðŸ“¥ RÃ©sultat dÃ©partements:', data);
      // L'API retourne soit un tableau, soit { value: [...] }
      const departmentsArray = Array.isArray(data) ? data : (data.value || []);
      setDepartmentsData(departmentsArray);
      console.log('âœ… DÃ©partements chargÃ©s:', departmentsArray.length);
    } catch (err) {
      console.error('âŒ Erreur chargement dÃ©partements:', err);
      setError('Erreur lors du chargement des dÃ©partements');
    } finally {
      setLoading(false);
    }
  };

  // Fonction pour charger les classes
  const loadClasses = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('ðŸ”„ Chargement des classes...');
      const data = await classeService.getAll();
      console.log('ðŸ“¥ RÃ©sultat classes:', data);
      // L'API retourne soit un tableau, soit { value: [...] }
      const classesArray = Array.isArray(data) ? data : (data.value || []);
      setClassesData(classesArray);
      console.log('âœ… Classes chargÃ©es:', classesArray.length);
    } catch (err) {
      console.error('âŒ Erreur chargement classes:', err);
      setError('Erreur lors du chargement des classes');
    } finally {
      setLoading(false);
    }
  };

  // Fonction pour supprimer un Ã©tudiant
  const handleDeleteStudent = async (id) => {
    if (!window.confirm('ÃŠtes-vous sÃ»r de vouloir supprimer cet Ã©tudiant ?')) return;
    
    try {
      const result = await etudiantService.delete ? await etudiantService.delete(id) : { success: false, message: 'Fonction non disponible' };
      if (result.success) {
        loadStudents();
        alert('Ã‰tudiant supprimÃ© avec succÃ¨s');
      } else {
        alert(result.message);
      }
    } catch (err) {
      alert('Erreur lors de la suppression');
    }
  };

  // Fonction pour supprimer un enseignant
  const handleDeleteTeacher = async (id) => {
    if (!window.confirm('ÃŠtes-vous sÃ»r de vouloir supprimer cet enseignant ?')) return;
    
    try {
      const result = await enseignantService.delete(id);
      if (result.success) {
        loadTeachers();
        alert('Enseignant supprimÃ© avec succÃ¨s');
      } else {
        alert(result.message);
      }
    } catch (err) {
      alert('Erreur lors de la suppression');
    }
  };

  // Fonction pour supprimer un dÃ©partement
  const handleDeleteDepartment = async (id) => {
    if (!window.confirm('ÃŠtes-vous sÃ»r de vouloir supprimer ce dÃ©partement ?')) return;
    
    try {
      const result = await departementService.delete(id);
      if (result.success) {
        loadDepartments();
        alert('DÃ©partement supprimÃ© avec succÃ¨s');
      } else {
        alert(result.message);
      }
    } catch (err) {
      alert('Erreur lors de la suppression');
    }
  };

  const recentActivities = [
    { type: 'user', message: 'Nouveau Ã©tudiant inscrit: Ahmed Ben Ali', time: 'Il y a 5 min' },
    { type: 'document', message: 'Rapport mensuel gÃ©nÃ©rÃ©', time: 'Il y a 2h' }
  ];

  const menuItems = [
    { id: 'dashboard', label: 'Tableau de bord', icon: LayoutDashboard },
    { id: 'students', label: 'Ã‰tudiants', icon: Users },
    { id: 'teachers', label: 'Enseignants', icon: UserCheck },
    { id: 'departments', label: 'DÃ©partements', icon: Building2 },
    { id: 'subjects', label: 'Classes', icon: BookOpen },
    { id: 'reports', label: 'Rapports', icon: FileText },
    { id: 'notifications', label: 'Notifications', icon: Bell },
    { id: 'profile', label: 'Mon Profil', icon: User }
  ];

  // Filtrer les donnÃ©es selon la recherche
  const filteredStudents = studentsData.filter(student => 
    student.nom?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.prenom?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.email?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const filteredTeachers = teachersData.filter(teacher => 
    teacher.nom?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    teacher.prenom?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    teacher.email?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const filteredDepartments = departmentsData.filter(dept => 
    dept.nom?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Rendu du Dashboard
  const renderDashboard = () => (
    <>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow">
          <div className="flex items-center justify-between mb-4">
            <div className="bg-blue-500 p-3 rounded-lg">
              <Users size={24} className="text-white" />
            </div>
            <span className="text-sm font-semibold text-blue-600">Actifs</span>
          </div>
          <h3 className="text-gray-500 text-sm font-medium mb-1">Ã‰tudiants</h3>
          <p className="text-3xl font-bold text-gray-800">{stats.students}</p>
        </div>

        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow">
          <div className="flex items-center justify-between mb-4">
            <div className="bg-green-500 p-3 rounded-lg">
              <UserCheck size={24} className="text-white" />
            </div>
            <span className="text-sm font-semibold text-green-600">Actifs</span>
          </div>
          <h3 className="text-gray-500 text-sm font-medium mb-1">Enseignants</h3>
          <p className="text-3xl font-bold text-gray-800">{stats.teachers}</p>
        </div>

        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow">
          <div className="flex items-center justify-between mb-4">
            <div className="bg-purple-500 p-3 rounded-lg">
              <Building2 size={24} className="text-white" />
            </div>
            <span className="text-sm font-semibold text-purple-600">Total</span>
          </div>
          <h3 className="text-gray-500 text-sm font-medium mb-1">DÃ©partements</h3>
          <p className="text-3xl font-bold text-gray-800">{stats.departments}</p>
        </div>

        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow">
          <div className="flex items-center justify-between mb-4">
            <div className="bg-orange-500 p-3 rounded-lg">
              <BookOpen size={24} className="text-white" />
            </div>
            <span className="text-sm font-semibold text-orange-600">Actives</span>
          </div>
          <h3 className="text-gray-500 text-sm font-medium mb-1">Classes</h3>
          <p className="text-3xl font-bold text-gray-800">{stats.classes}</p>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2 bg-white rounded-xl shadow-sm border border-gray-200 p-6">
          <div className="flex items-center justify-between mb-6">
            <h3 className="text-lg font-bold text-gray-800">Statistiques par dÃ©partement</h3>
            <button 
              onClick={() => setActiveMenu('departments')} 
              className="text-sm text-blue-600 hover:text-blue-700 font-medium"
            >
              Voir tout
            </button>
          </div>
          {loading ? (
