import React, { useState } from 'react';

/**
 * Composant de validation de mot de passe
 * Valide : longueur minimale, majuscule, minuscule, chiffre
 */
const PasswordValidator = ({ password, showValidation = true }) => {
  const validations = [
    {
      test: (pwd) => pwd.length >= 8,
      message: 'Au moins 8 caractères',
      met: password.length >= 8
    },
    {
      test: (pwd) => /[A-Z]/.test(pwd),
      message: 'Au moins une majuscule',
      met: /[A-Z]/.test(password)
    },
    {
      test: (pwd) => /[a-z]/.test(pwd),
      message: 'Au moins une minuscule',
      met: /[a-z]/.test(password)
    },
    {
      test: (pwd) => /[0-9]/.test(pwd),
      message: 'Au moins un chiffre',
      met: /[0-9]/.test(password)
    }
  ];

  const allValid = validations.every(v => v.met);

  if (!showValidation || !password) return null;

  return (
    <div style={styles.container}>
      <div style={styles.title}>Exigences du mot de passe :</div>
      {validations.map((validation, index) => (
        <div key={index} style={styles.requirement}>
          <span style={{
            ...styles.icon,
            color: validation.met ? '#22c55e' : '#ef4444'
          }}>
            {validation.met ? '✓' : '✗'}
          </span>
          <span style={{
            ...styles.text,
            color: validation.met ? '#22c55e' : '#64748b'
          }}>
            {validation.message}
          </span>
        </div>
      ))}
      {allValid && (
        <div style={styles.successMessage}>
          ✅ Mot de passe valide !
        </div>
      )}
    </div>
  );
};

/**
 * Hook pour valider un mot de passe
 */
export const usePasswordValidation = (password) => {
  const validations = {
    minLength: password.length >= 8,
    hasUpperCase: /[A-Z]/.test(password),
    hasLowerCase: /[a-z]/.test(password),
    hasNumber: /[0-9]/.test(password)
  };

  const isValid = Object.values(validations).every(v => v);

  const getErrorMessage = () => {
    if (!validations.minLength) return 'Le mot de passe doit contenir au moins 8 caractères';
    if (!validations.hasUpperCase) return 'Le mot de passe doit contenir au moins une majuscule';
    if (!validations.hasLowerCase) return 'Le mot de passe doit contenir au moins une minuscule';
    if (!validations.hasNumber) return 'Le mot de passe doit contenir au moins un chiffre';
    return '';
  };

  return {
    isValid,
    validations,
    errorMessage: getErrorMessage()
  };
};

const styles = {
  container: {
    marginTop: '0.75rem',
    padding: '1rem',
    backgroundColor: '#f8fafc',
    borderRadius: '8px',
    border: '1px solid #e2e8f0'
  },
  title: {
    fontSize: '0.875rem',
    fontWeight: '600',
    color: '#475569',
    marginBottom: '0.5rem'
  },
  requirement: {
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
    padding: '0.25rem 0',
    fontSize: '0.875rem'
  },
  icon: {
    fontWeight: 'bold',
    fontSize: '1rem'
  },
  text: {
    fontWeight: '500'
  },
  successMessage: {
    marginTop: '0.5rem',
    padding: '0.5rem',
    backgroundColor: '#dcfce7',
    color: '#166534',
    borderRadius: '6px',
    fontSize: '0.875rem',
    fontWeight: '600',
    textAlign: 'center'
  }
};

export default PasswordValidator;
