import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import { statsService } from "../services/adminServices";
import "./Dashboard.css";

const DirectorDashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showProfile, setShowProfile] = useState(false);
  const [editingProfile, setEditingProfile] = useState(false);
  const [profileData, setProfileData] = useState({
    prenom: user?.prenom || "",
    nom: user?.nom || "",
    email: user?.email || "",
    telephone: user?.telephone || "",
    departement: user?.departement || "",
    specialite: user?.specialite || "",
  });

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      // R√©cup√©rer les vraies stats depuis le backend
      const result = await statsService.getDirectorStats();
      
      const data = {
        title: "Espace Directeur de D√©partement",
        stats: [
          { 
            label: "Enseignants", 
            value: result.success ? result.data.totalEnseignants.toString() : "0", 
            icon: "üë®‚Äçüè´" 
          },
          { 
            label: "√âtudiants", 
            value: result.success ? result.data.totalEtudiants.toString() : "0", 
            icon: "üë•" 
          },
          { 
            label: "Classes", 
            value: result.success ? result.data.totalClasses.toString() : "0", 
            icon: "üìö" 
          },
          { 
            label: "D√©partements", 
            value: result.success ? result.data.totalDepartements.toString() : "0", 
            icon: "üè¢" 
          },
        ],
        actions: [
          { label: "G√©rer d√©partements", icon: "üè¢", action: "manageDepartements" },
          { label: "G√©rer utilisateurs", icon: "üë•", action: "manageUsers" },
          { label: "G√©rer enseignants", icon: "üë®‚Äçüè´", action: "manageTeachers" },
          { label: "G√©rer √©tudiants", icon: "üë•", action: "manageStudents" },
          { label: "Planifier cours", icon: "üìÖ", action: "scheduleCourses" },
          { label: "Rapports", icon: "üìä", action: "reports" },
          { label: "Budget", icon: "üí∞", action: "budget" },
          { label: "√âvaluations", icon: "üìù", action: "evaluations" },
        ],
      };
      setDashboardData(data);
      setLoading(false);
    } catch (error) {
      console.error("Erreur chargement dashboard:", error);
      setLoading(false);
    }
  };

  const handleAction = (action) => {
    switch (action) {
      case "manageUsers":
        navigate("/admin-panel");
        break;
      case "manageTeachers":
        alert("Ouverture de la gestion des enseignants...");
        break;
      case "manageStudents":
        alert("Ouverture de la gestion des √©tudiants...");
        break;
      case "scheduleCourses":
        alert("Ouverture de la planification des cours...");
        break;
      case "reports":
        alert("Ouverture des rapports...");
        break;
      case "budget":
        alert("Ouverture de la gestion budg√©taire...");
        break;
      case "evaluations":
        alert("Ouverture des √©valuations...");
        break;
      default:
        alert(`Action ${action} non impl√©ment√©e`);
    }
  };

  const handleProfileUpdate = async () => {
    if (!profileData.prenom || !profileData.nom || !profileData.email) {
      alert("Veuillez remplir tous les champs obligatoires");
      return;
    }

    try {
      // Simulate API call to update profile
      await new Promise((resolve) => setTimeout(resolve, 1000));
      alert("Profil mis √† jour avec succ√®s!");
      setEditingProfile(false);
    } catch (error) {
      alert("Erreur lors de la mise √† jour du profil");
    }
  };

  const handleLogout = () => {
    logout();
    navigate("/");
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="loading-spinner"></div>
        <p>Chargement de l'espace directeur...</p>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <div className="header-content">
          <div className="header-left">
            <div className="header-info">
              <h1>{dashboardData?.title}</h1>
              <div className="user-info">
                <span className="user-name">
                  {user?.prenom} {user?.nom}
                </span>
                <span className="user-role">Directeur de D√©partement</span>
              </div>
            </div>
          </div>
          <div className="header-right">
            <button
              className="profile-btn"
              onClick={() => setShowProfile(!showProfile)}
            >
              üë§ Profil
            </button>
            <button className="logout-btn" onClick={handleLogout}>
              D√©connexion
            </button>
          </div>
        </div>
      </header>

      {showProfile && (
        <div className="profile-modal">
          <div className="profile-content">
            <h3>Informations du profil</h3>
            {editingProfile ? (
              <div className="profile-edit">
                <div className="form-group">
                  <label>Pr√©nom:</label>
                  <input
                    type="text"
                    value={profileData.prenom}
                    onChange={(e) =>
                      setProfileData({ ...profileData, prenom: e.target.value })
                    }
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Nom:</label>
                  <input
                    type="text"
                    value={profileData.nom}
                    onChange={(e) =>
                      setProfileData({ ...profileData, nom: e.target.value })
                    }
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Email:</label>
                  <input
                    type="email"
                    value={profileData.email}
                    onChange={(e) =>
                      setProfileData({ ...profileData, email: e.target.value })
                    }
                    required
                  />
                </div>
                <div className="form-group">
                  <label>T√©l√©phone:</label>
                  <input
                    type="tel"
                    value={profileData.telephone}
                    onChange={(e) =>
                      setProfileData({
                        ...profileData,
                        telephone: e.target.value,
                      })
                    }
                  />
                </div>
                <div className="form-group">
                  <label>D√©partement:</label>
                  <input
                    type="text"
                    value={profileData.departement}
                    onChange={(e) =>
                      setProfileData({
                        ...profileData,
                        departement: e.target.value,
                      })
                    }
                  />
                </div>
                <div className="form-group">
                  <label>Sp√©cialit√©:</label>
                  <input
                    type="text"
                    value={profileData.specialite}
                    onChange={(e) =>
                      setProfileData({
                        ...profileData,
                        specialite: e.target.value,
                      })
                    }
                  />
                </div>
                <div className="profile-actions">
                  <button onClick={handleProfileUpdate}>Sauvegarder</button>
                  <button onClick={() => setEditingProfile(false)}>
                    Annuler
                  </button>
                </div>
              </div>
            ) : (
              <div className="profile-view">
                <p>
                  <strong>Pr√©nom:</strong> {user?.prenom}
                </p>
                <p>
                  <strong>Nom:</strong> {user?.nom}
                </p>
                <p>
                  <strong>Email:</strong> {user?.email}
                </p>
                <p>
                  <strong>T√©l√©phone:</strong>{" "}
                  {user?.telephone || "Non sp√©cifi√©"}
                </p>
                <p>
                  <strong>D√©partement:</strong>{" "}
                  {user?.departement || "Non sp√©cifi√©"}
                </p>
                <p>
                  <strong>Sp√©cialit√©:</strong>{" "}
                  {user?.specialite || "Non sp√©cifi√©e"}
                </p>
                <p>
                  <strong>R√¥le:</strong> Directeur de D√©partement
                </p>
                <button onClick={() => setEditingProfile(true)}>
                  Modifier
                </button>
              </div>
            )}
            <button
              className="close-profile"
              onClick={() => setShowProfile(false)}
            >
              √ó
            </button>
          </div>
        </div>
      )}

      <div className="dashboard-content">
        <div className="stats-grid">
          {dashboardData?.stats.map((stat, index) => (
            <div key={index} className="stat-card">
              <div className="stat-icon">{stat.icon}</div>
              <div className="stat-info">
                <h3>{stat.value}</h3>
                <p>{stat.label}</p>
              </div>
            </div>
          ))}
        </div>

        <div className="actions-section">
          <h2>Actions disponibles</h2>
          <div className="actions-grid">
            {dashboardData?.actions.map((action, index) => (
              <button
                key={index}
                className="action-btn"
                onClick={() => handleAction(action.action)}
              >
                <span className="action-icon">{action.icon}</span>
                <span className="action-label">{action.label}</span>
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DirectorDashboard;
