import { adminApi } from "../config/api";

// Service pour gérer les départements via l'admin-service (port 3000)
export const departementService = {
  // Récupérer tous les départements
  getAll: async () => {
    try {
      const response = await adminApi.get("/departement");
      return { success: true, data: response.data };
    } catch (error) {
      console.error("Erreur lors de la récupération des départements:", error);
      return {
        success: false,
        message: error.response?.data?.message || "Erreur de récupération",
      };
    }
  },

  // Récupérer un département par ID
  getById: async (id) => {
    try {
      const response = await adminApi.get(`/departement/${id}`);
      return { success: true, data: response.data };
    } catch (error) {
      console.error("Erreur lors de la récupération du département:", error);
      return {
        success: false,
        message: error.response?.data?.message || "Département non trouvé",
      };
    }
  },

  // Créer un nouveau département
  create: async (departementData) => {
    try {
      const response = await adminApi.post("/departement", departementData);
      return { success: true, data: response.data };
    } catch (error) {
      console.error("Erreur lors de la création du département:", error);
      return {
        success: false,
        message: error.response?.data?.message || "Erreur de création",
      };
    }
  },

  // Mettre à jour un département
  update: async (id, departementData) => {
    try {
      const response = await adminApi.patch(`/departement/${id}`, departementData);
      return { success: true, data: response.data };
    } catch (error) {
      console.error("Erreur lors de la mise à jour du département:", error);
      return {
        success: false,
        message: error.response?.data?.message || "Erreur de mise à jour",
      };
    }
  },

  // Supprimer un département
  delete: async (id) => {
    try {
      await adminApi.delete(`/departement/${id}`);
      return { success: true };
    } catch (error) {
      console.error("Erreur lors de la suppression du département:", error);
      return {
        success: false,
        message: error.response?.data?.message || "Erreur de suppression",
      };
    }
  },
};
