sourcesContent:
import React, { useState, useEffect } from 'react';
import { LayoutDashboard, Users, BookOpen, Building2, UserCheck, FileText, Bell, Menu, X, TrendingUp, AlertCircle, Plus, Edit, Trash2, Search, Loader, LogOut, User } from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { departementService, enseignantService, etudiantService, classeService } from '../services/adminServices';
import AddStudentModal from './AddStudentModal';
import AddTeacherModal from './AddTeacherModal';
import AddDepartmentModal from './AddDepartmentModal';
import AddClasseModal from './AddClasseModal';
import EditStudentModal from './EditStudentModal';
import EditTeacherModal from './EditTeacherModal';
import EditDepartmentModal from './EditDepartmentModal';
import EditClasseModal from './EditClasseModal';

const AdminDashboard = () => {
  const { user, logout, updateUser } = useAuth();
  const navigate = useNavigate();
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [activeMenu, setActiveMenu] = useState('dashboard');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Ã‰tats pour les modaux
  const [showAddStudentModal, setShowAddStudentModal] = useState(false);
  const [showAddTeacherModal, setShowAddTeacherModal] = useState(false);
  const [showAddDepartmentModal, setShowAddDepartmentModal] = useState(false);
  const [showAddClasseModal, setShowAddClasseModal] = useState(false);
  
  // Ã‰tats pour les modaux de modification
  const [showEditStudentModal, setShowEditStudentModal] = useState(false);
  const [showEditTeacherModal, setShowEditTeacherModal] = useState(false);
  const [showEditDepartmentModal, setShowEditDepartmentModal] = useState(false);
  const [showEditClasseModal, setShowEditClasseModal] = useState(false);
  
  // Ã‰tats pour les Ã©lÃ©ments sÃ©lectionnÃ©s
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [selectedTeacher, setSelectedTeacher] = useState(null);
  const [selectedDepartment, setSelectedDepartment] = useState(null);
  const [selectedClasse, setSelectedClasse] = useState(null);

  // Ã‰tats pour les donnÃ©es
  const [stats, setStats] = useState({
    students: 0,
    teachers: 0,
    departments: 0,
    classes: 0
  });
  const [studentsData, setStudentsData] = useState([]);
  const [teachersData, setTeachersData] = useState([]);
  const [departmentsData, setDepartmentsData] = useState([]);
  const [classesData, setClassesData] = useState([]);
  const [studentSearchTerm, setStudentSearchTerm] = useState('');
  const [teacherSearchTerm, setTeacherSearchTerm] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedDepartmentFilter, setSelectedDepartmentFilter] = useState('');
  const [selectedClasseFilter, setSelectedClasseFilter] = useState('');

  // Ã‰tats pour le profil
  const [isEditingProfile, setIsEditingProfile] = useState(false);
  const [profileData, setProfileData] = useState({
    nom: user?.nom || '',
    prenom: user?.prenom || '',
    email: user?.email || '',
    cin: user?.cin || '',
    role: user?.role || 'Administrateur'
  });

  // Mettre Ã  jour profileData quand user change
  useEffect(() => {
    if (user) {
      setProfileData({
        nom: user.nom || '',
        prenom: user.prenom || '',
        email: user.email || '',
        cin: user.cin || '',
        role: user.role || 'Administrateur'
      });
    }
  }, [user]);

  // Chargement des statistiques au dÃ©marrage
  useEffect(() => {
    loadDashboardStats();
    // Charger dÃ©partements et classes pour les filtres
    loadDepartments();
    loadClasses();
  }, []);

  // Chargement des donnÃ©es selon le menu actif
  useEffect(() => {
    switch (activeMenu) {
      case 'students':
        loadStudents();
        break;
      case 'teachers':
        loadTeachers();
        break;
      case 'departments':
        loadDepartments();
        break;
      case 'subjects':
        loadClasses();
        break;
      default:
        break;
    }
  }, [activeMenu]);

  // Fonction pour charger les statistiques
  const loadDashboardStats = async () => {
    try {
      setLoading(true);
      setError(null);
      
      // Charger les donnÃ©es directement
      const [deptsData, ensData, etuData, classesData] = await Promise.all([
        departementService.getAll().catch(() => []),
        enseignantService.getAll().catch(() => ({ success: false, data: [] })),
        etudiantService.getAll().catch(() => ({ success: false, data: [] })),
        classeService.getAll().catch(() => [])
      ]);

      // GÃ©rer le format { value: [...] } ou tableau direct
      const deptsArray = Array.isArray(deptsData) ? deptsData : (deptsData.value || deptsData.data || []);
      const ensArray = ensData.success ? (ensData.data || []) : (Array.isArray(ensData) ? ensData : (ensData.value || []));
      const etuArray = etuData.success ? (etuData.data || []) : (Array.isArray(etuData) ? etuData : (etuData.value || []));
      const classesArray = Array.isArray(classesData) ? classesData : (classesData.value || classesData.data || []);

      setStats({
        students: etuArray.length || 0,
        teachers: ensArray.length || 0,
        departments: deptsArray.length || 0,
        classes: classesArray.length || 0
      });
      
      console.log('ğŸ“Š Statistiques chargÃ©es:', {
        students: etuArray.length,
        teachers: ensArray.length,
        departments: deptsArray.length,
        classes: classesArray.length
      });
    } catch (err) {
      console.error('âŒ Erreur chargement stats:', err);
      setError('Erreur lors du chargement des statistiques');
    } finally {
      setLoading(false);
    }
  };

  // Fonction pour charger les Ã©tudiants
  const loadStudents = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('ğŸ”„ Chargement des Ã©tudiants...');
      const result = await etudiantService.getAll();
      console.log('ğŸ“¥ RÃ©sultat Ã©tudiants:', result);
      if (result.success) {
        setStudentsData(result.data || []);
        console.log('âœ… Ã‰tudiants chargÃ©s:', result.data?.length || 0);
        if (result.data?.length > 0) {
          console.log('ğŸ” Structure du premier Ã©tudiant:', result.data[0]);
          console.log('ğŸ” Classe du premier Ã©tudiant:', result.data[0]?.classe);
        }
      } else {
        setError(result.message);
        console.error('âŒ Erreur:', result.message);
      }
    } catch (err) {
      console.error('âŒ Erreur chargement Ã©tudiants:', err);
      setError('Erreur lors du chargement des Ã©tudiants');
    } finally {
      setLoading(false);
    }
  };

  // Fonction pour charger les enseignants
  const loadTeachers = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('ğŸ”„ Chargement des enseignants...');
      const result = await enseignantService.getAll();
      console.log('ğŸ“¥ RÃ©sultat enseignants:', result);
      if (result.success) {
        setTeachersData(result.data || []);
        console.log('âœ… Enseignants chargÃ©s:', result.data?.length || 0);
      } else {
        setError(result.message);
        console.error('âŒ Erreur:', result.message);
      }
    } catch (err) {
      console.error('âŒ Erreur chargement enseignants:', err);
      setError('Erreur lors du chargement des enseignants');
    } finally {
      setLoading(false);
    }
  };

  // Fonction pour charger les dÃ©partements
  const loadDepartments = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('ğŸ”„ Chargement des dÃ©partements...');
      const data = await departementService.getAll();
      console.log('ğŸ“¥ RÃ©sultat dÃ©partements:', data);
      // L'API retourne soit un tableau, soit { value: [...] }
      const departmentsArray = Array.isArray(data) ? data : (data.value || []);
      setDepartmentsData(departmentsArray);
      console.log('âœ… DÃ©partements chargÃ©s:', departmentsArray.length);
    } catch (err) {
      console.error('âŒ Erreur chargement dÃ©partements:', err);
      setError('Erreur lors du chargement des dÃ©partements');
    } finally {
      setLoading(false);
    }
  };

  // Fonction pour charger les classes
  const loadClasses = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('ğŸ”„ Chargement des classes...');
      const data = await classeService.getAll();
      console.log('ğŸ“¥ RÃ©sultat classes:', data);
      // L'API retourne soit un tableau, soit { value: [...] }
      const classesArray = Array.isArray(data) ? data : (data.value || []);
      setClassesData(classesArray);
      console.log('âœ… Classes chargÃ©es:', classesArray.length);
      if (classesArray.length > 0) {
        console.log('ğŸ” Structure de la premiÃ¨re classe:', classesArray[0]);
      }
    } catch (err) {
      console.error('âŒ Erreur chargement classes:', err);
      setError('Erreur lors du chargement des classes');
    } finally {
      setLoading(false);
    }
  };

  // Fonction pour supprimer un Ã©tudiant
  const handleDeleteStudent = async (id) => {
    if (!window.confirm('ÃŠtes-vous sÃ»r de vouloir supprimer cet Ã©tudiant ?')) return;
    
    try {
      const result = await etudiantService.delete(id);
      if (result.success) {
        loadStudents();
        loadDashboardStats();
        alert('âœ… Ã‰tudiant supprimÃ© avec succÃ¨s');
      } else {
        alert(`âŒ ${result.message}`);
      }
    } catch (err) {
      alert('âŒ Erreur lors de la suppression');
    }
  };

  // Fonction pour modifier un Ã©tudiant
  const handleEditStudent = (student) => {
    setSelectedStudent(student);
    setShowEditStudentModal(true);
  };

  // Fonction pour supprimer un enseignant
  const handleDeleteTeacher = async (id) => {
    if (!window.confirm('ÃŠtes-vous sÃ»r de vouloir supprimer cet enseignant ?')) return;
    
    try {
      const result = await enseignantService.delete(id);
      if (result.success) {
        loadTeachers();
        loadDashboardStats();
        alert('âœ… Enseignant supprimÃ© avec succÃ¨s');
      } else {
        alert(`âŒ ${result.message}`);
      }
    } catch (err) {
      alert('âŒ Erreur lors de la suppression');
    }
  };

  // Fonction pour modifier un enseignant
  const handleEditTeacher = (teacher) => {
    setSelectedTeacher(teacher);
    setShowEditTeacherModal(true);
  };

  // Fonction pour supprimer un dÃ©partement
  const handleDeleteDepartment = async (id) => {
    if (!window.confirm('ÃŠtes-vous sÃ»r de vouloir supprimer ce dÃ©partement ?')) return;
    
    try {
      const result = await departementService.delete(id);
      if (result.success) {
        loadDepartments();
        loadDashboardStats();
        alert('âœ… DÃ©partement supprimÃ© avec succÃ¨s');
      } else {
        alert(`âŒ ${result.message}`);
      }
    } catch (err) {
      alert('âŒ Erreur lors de la suppression');
    }
  };

  // Fonction pour modifier un dÃ©partement
  const handleEditDepartment = (department) => {
    setSelectedDepartment(department);
    setShowEditDepartmentModal(true);
  };

  // Fonction pour supprimer une classe
  const handleDeleteClasse = async (id) => {
    if (!window.confirm('ÃŠtes-vous sÃ»r de vouloir supprimer cette classe ?')) return;
    
    try {
      const result = await classeService.delete(id);
      if (result.success) {
        loadClasses();
        loadDashboardStats();
        alert('âœ… Classe supprimÃ©e avec succÃ¨s');
      } else {
        alert(`âŒ ${result.message}`);
      }
    } catch (err) {
      alert('âŒ Erreur lors de la suppression');
    }
  };

  // Fonction pour modifier une classe
  const handleEditClasse = (classe) => {
    setSelectedClasse(classe);
    setShowEditClasseModal(true);
  };

  const recentActivities = [
    { type: 'user', message: 'Nouveau Ã©tudiant inscrit: Ahmed Ben Ali', time: 'Il y a 5 min' },
    { type: 'document', message: 'Rapport mensuel gÃ©nÃ©rÃ©', time: 'Il y a 2h' }
  ];

  const menuItems = [
    { id: 'dashboard', label: 'Tableau de bord', icon: LayoutDashboard },
    { id: 'students', label: 'Ã‰tudiants', icon: Users },
    { id: 'teachers', label: 'Enseignants', icon: UserCheck },
    { id: 'departments', label: 'DÃ©partements', icon: Building2 },
    { id: 'subjects', label: 'Classes', icon: BookOpen },
    { id: 'reports', label: 'Rapports', icon: FileText },
    { id: 'notifications', label: 'Notifications', icon: Bell },
    { id: 'profile', label: 'Mon Profil', icon: User }
  ];

  // Filtrer les donnÃ©es selon la recherche
  const filteredStudents = studentsData.filter(student => {
    const matchesSearch = 
      student.nom?.toLowerCase().includes(studentSearchTerm.toLowerCase()) ||
      student.prenom?.toLowerCase().includes(studentSearchTerm.toLowerCase()) ||
      student.email?.toLowerCase().includes(studentSearchTerm.toLowerCase()) ||
      student.cin?.toLowerCase().includes(studentSearchTerm.toLowerCase());
    
    // Filtrer par classe d'abord
    let matchesClasse = true;
    if (selectedClasseFilter) {
      const classeId = typeof student.classe === 'object' ? student.classe?.id : student.classe;
      matchesClasse = classeId == selectedClasseFilter;
    }
    
    // Filtrer par dÃ©partement (via la classe de l'Ã©tudiant)
    let matchesDepartment = true;
    if (selectedDepartmentFilter && !selectedClasseFilter) {
      // Trouver la classe de l'Ã©tudiant dans classesData
      const studentClasseId = typeof student.classe === 'object' ? student.classe?.id : student.classe;
      const classeObj = classesData.find(c => c.id == studentClasseId);
      
      if (classeObj) {
        // Le dÃ©partement est dans specialite.departement
