      <div className="space-y-4">
        {recentActivities.map((activity, index) => (
          <div key={index} className="border border-gray-200 rounded-lg p-4 hover:bg-gray-50 transition-colors">
            <div className="flex gap-4">
              <div className="w-10 h-10 bg-blue-50 rounded-full flex items-center justify-center flex-shrink-0">
                {activity.type === 'user' && <Users size={20} className="text-blue-600" />}
                {activity.type === 'alert' && <AlertCircle size={20} className="text-orange-600" />}
                {activity.type === 'document' && <FileText size={20} className="text-purple-600" />}
              </div>
              <div className="flex-1">
                <p className="text-sm text-gray-800 font-medium mb-1">{activity.message}</p>
                <p className="text-xs text-gray-500">{activity.time}</p>
              </div>
              <button className="text-sm text-blue-600 hover:text-blue-700 font-medium">Marquer lu</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  // Rendu de la page Profil
  const renderProfile = () => {
    const handleChange = (e) => {
      const { name, value } = e.target;
      setProfileData(prev => ({ ...prev, [name]: value }));
    };

    const handleSave = async () => {
      // Mettre Ã  jour le contexte utilisateur via l'API
      const result = await updateUser(profileData);
      
      if (result.success) {
        setIsEditingProfile(false);
        alert('âœ… Profil mis Ã  jour avec succÃ¨s!');
        console.log('Profil sauvegardÃ©:', result.user);
      } else {
        alert('âŒ ' + (result.message || 'Erreur lors de la mise Ã  jour du profil'));
      }
    };

    return (
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden">
          {/* En-tÃªte du profil */}
          <div className="bg-gradient-to-r from-blue-600 to-purple-600 p-8 text-white">
            <div className="flex items-center gap-6">
              <div className="w-24 h-24 bg-white rounded-full flex items-center justify-center">
                <User size={48} className="text-blue-600" />
              </div>
              <div>
                <h2 className="text-3xl font-bold">{user?.prenom} {user?.nom}</h2>
                <p className="text-blue-100 mt-1">{user?.role || 'Administrateur'}</p>
                <p className="text-blue-100 text-sm mt-1">{user?.email}</p>
              </div>
            </div>
          </div>

          {/* Contenu du profil */}
          <div className="p-8">
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-2xl font-bold text-gray-800">Informations personnelles</h3>
              {!isEditingProfile && (
                <button
                  onClick={() => setIsEditingProfile(true)}
                  className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                >
                  <Edit size={18} />
                  Modifier
                </button>
              )}
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Nom</label>
                <input
                  type="text"
                  name="nom"
                  value={profileData.nom}
                  onChange={handleChange}
                  disabled={!isEditingProfile}
                  className={`w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                    !isEditingProfile ? 'bg-gray-50 text-gray-600' : ''
                  }`}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">PrÃ©nom</label>
                <input
                  type="text"
                  name="prenom"
                  value={profileData.prenom}
                  onChange={handleChange}
                  disabled={!isEditingProfile}
                  className={`w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                    !isEditingProfile ? 'bg-gray-50 text-gray-600' : ''
                  }`}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Email</label>
                <input
                  type="email"
                  name="email"
                  value={profileData.email}
                  onChange={handleChange}
                  disabled={!isEditingProfile}
                  className={`w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                    !isEditingProfile ? 'bg-gray-50 text-gray-600' : ''
                  }`}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">CIN</label>
                <input
                  type="text"
                  name="cin"
                  value={profileData.cin}
                  onChange={handleChange}
                  maxLength="8"
                  disabled={!isEditingProfile}
                  className={`w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                    !isEditingProfile ? 'bg-gray-50 text-gray-600' : ''
                  }`}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">RÃ´le</label>
                <input
                  type="text"
                  name="role"
                  value={profileData.role}
                  disabled
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg bg-gray-50 text-gray-600"
                />
              </div>
            </div>

            {isEditingProfile && (
              <div className="flex gap-3 mt-6">
                <button
                  onClick={handleSave}
                  className="px-6 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors font-medium"
                >
                  Enregistrer les modifications
                </button>
                <button
                  onClick={() => {
                    setIsEditingProfile(false);
                    setProfileData({
                      nom: user?.nom || '',
                      prenom: user?.prenom || '',
                      email: user?.email || '',
                      telephone: user?.telephone || '',
                      role: user?.role || 'Administrateur'
                    });
                  }}
                  className="px-6 py-3 bg-gray-300 text-gray-700 rounded-lg hover:bg-gray-400 transition-colors font-medium"
                >
                  Annuler
                </button>
              </div>
            )}

            {/* Bouton de dÃ©connexion */}
            <div className="mt-8 pt-8 border-t border-gray-200">
              <button
                onClick={() => {
                  if (window.confirm('ÃŠtes-vous sÃ»r de vouloir vous dÃ©connecter ?')) {
                    logout();
                    navigate('/login');
                  }
                }}
                className="flex items-center gap-2 px-6 py-3 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors font-medium"
              >
                <LogOut size={18} />
                Se dÃ©connecter
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  };

  // Fonction pour rendre le contenu selon le menu actif
  const renderContent = () => {
    switch (activeMenu) {
      case 'dashboard': return renderDashboard();
      case 'students': return renderStudents();
      case 'teachers': return renderTeachers();
      case 'departments': return renderDepartments();
      case 'subjects': return renderSubjects();
      case 'reports': return renderReports();
      case 'notifications': return renderNotifications();
      case 'profile': return renderProfile();
      default: return renderDashboard();
    }
  };

  return (
    <div className="flex h-screen bg-gray-50">
      {/* Sidebar */}
      <aside className={`${sidebarOpen ? 'w-64' : 'w-20'} bg-gradient-to-b from-blue-900 to-blue-800 text-white transition-all duration-300 flex flex-col`}>
        <div className="p-4 flex items-center justify-between border-b border-blue-700">
          {sidebarOpen && <h1 className="text-xl font-bold">ISET Tozeur</h1>}
          <button onClick={() => setSidebarOpen(!sidebarOpen)} className="p-2 hover:bg-blue-700 rounded-lg">
            {sidebarOpen ? <X size={20} /> : <Menu size={20} />}
          </button>
        </div>

        <nav className="flex-1 p-4 space-y-2 overflow-y-auto">
          {menuItems.map((item) => (
            <button
              key={item.id}
              onClick={() => setActiveMenu(item.id)}
              className={`w-full flex items-center gap-3 px-4 py-3 rounded-lg transition-all ${
                activeMenu === item.id
                  ? 'bg-blue-700 shadow-lg'
                  : 'hover:bg-blue-800'
              }`}
            >
              <item.icon size={20} />
              {sidebarOpen && <span className="text-sm font-medium">{item.label}</span>}
            </button>
          ))}
        </nav>

        <div className="p-4 border-t border-blue-700">
          <div className="space-y-3">
            {/* Profil utilisateur */}
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-blue-600 rounded-full flex items-center justify-center font-bold text-sm">
                {user?.nom ? user.nom.charAt(0).toUpperCase() : 'A'}
                {user?.prenom ? user.prenom.charAt(0).toUpperCase() : 'D'}
              </div>
              {sidebarOpen && (
                <div className="flex-1 overflow-hidden">
                  <p className="text-sm font-medium truncate">
                    {user?.prenom} {user?.nom}
                  </p>
                  <p className="text-xs text-blue-300 truncate">{user?.email}</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </aside>

      {/* Main Content */}
      <main className="flex-1 overflow-auto">
        {/* Header */}
        <header className="bg-white shadow-sm border-b border-gray-200 px-8 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-2xl font-bold text-gray-800">
                {menuItems.find(item => item.id === activeMenu)?.label || 'Tableau de bord'}
              </h2>
              <p className="text-sm text-gray-500">Plateforme de gestion universitaire</p>
            </div>
            <div className="flex items-center gap-4">
              <button className="relative p-2 hover:bg-gray-100 rounded-full">
                <Bell size={20} className="text-gray-600" />
                <span className="absolute top-1 right-1 w-2 h-2 bg-red-500 rounded-full"></span>
              </button>
              <button 
                onClick={loadDashboardStats}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors flex items-center gap-2"
              >
                <TrendingUp size={18} />
                Actualiser
              </button>
            </div>
          </div>
        </header>

        {/* Dashboard Content */}
        <div className="p-8">
          {renderContent()}
        </div>
      </main>

      {/* Modaux */}
      <AddStudentModal 
        isOpen={showAddStudentModal}
        onClose={() => setShowAddStudentModal(false)}
        onStudentAdded={() => {
          loadStudents();
          loadDashboardStats();
        }}
      />

      <AddTeacherModal 
        isOpen={showAddTeacherModal}
        onClose={() => setShowAddTeacherModal(false)}
        onTeacherAdded={() => {
          loadTeachers();
          loadDashboardStats();
        }}
      />

      <AddDepartmentModal 
        isOpen={showAddDepartmentModal}
        onClose={() => setShowAddDepartmentModal(false)}
        onDepartmentAdded={() => {
          loadDepartments();
          loadDashboardStats();
        }}
      />

      <AddClasseModal 
        isOpen={showAddClasseModal}
        onClose={() => setShowAddClasseModal(false)}
        onClasseAdded={() => {
          loadClasses();
          loadDashboardStats();
        }}
      />

      {/* Modaux de modification */}
      <EditStudentModal 
        isOpen={showEditStudentModal}
        onClose={() => {
          setShowEditStudentModal(false);
          setSelectedStudent(null);
        }}
        onStudentUpdated={() => {
          loadStudents();
          loadDashboardStats();
        }}
        student={selectedStudent}
      />

      <EditTeacherModal 
        isOpen={showEditTeacherModal}
        onClose={() => {
          setShowEditTeacherModal(false);
          setSelectedTeacher(null);
        }}
        onTeacherUpdated={() => {
          loadTeachers();
          loadDashboardStats();
        }}
        teacher={selectedTeacher}
      />

      <EditDepartmentModal 
        isOpen={showEditDepartmentModal}
        onClose={() => {
          setShowEditDepartmentModal(false);
          setSelectedDepartment(null);
        }}
        onDepartmentUpdated={() => {
          loadDepartments();
          loadDashboardStats();
        }}
        department={selectedDepartment}
      />

      <EditClasseModal 
        isOpen={showEditClasseModal}
        onClose={() => {
          setShowEditClasseModal(false);
          setSelectedClasse(null);
        }}
        onClasseUpdated={() => {
          loadClasses();
          loadDashboardStats();
        }}
        classe={selectedClasse}
      />
    </div>
  );
};

export default AdminDashboard;
