import React, { useState, useEffect } from "react";
import { departementService } from "../services/departementService";
import "./DepartementManagement.css";

const DepartementManagement = () => {
  const [departements, setDepartements] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingDept, setEditingDept] = useState(null);
  const [formData, setFormData] = useState({ nom: "" });

  // Charger les d√©partements au montage du composant
  useEffect(() => {
    loadDepartements();
  }, []);

  const loadDepartements = async () => {
    setLoading(true);
    setError(null);
    
    const result = await departementService.getAll();
    
    if (result.success) {
      setDepartements(result.data);
    } else {
      setError(result.message);
    }
    
    setLoading(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.nom.trim()) {
      alert("Le nom du d√©partement est obligatoire");
      return;
    }

    let result;
    if (editingDept) {
      // Mise √† jour
      result = await departementService.update(editingDept.id, formData);
    } else {
      // Cr√©ation
      result = await departementService.create(formData);
    }

    if (result.success) {
      alert(editingDept ? "D√©partement mis √† jour !" : "D√©partement cr√©√© !");
      setFormData({ nom: "" });
      setShowAddForm(false);
      setEditingDept(null);
      loadDepartements(); // Recharger la liste
    } else {
      alert("Erreur : " + result.message);
    }
  };

  const handleEdit = (dept) => {
    setEditingDept(dept);
    setFormData({ nom: dept.nom });
    setShowAddForm(true);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("√ätes-vous s√ªr de vouloir supprimer ce d√©partement ?")) {
      return;
    }

    const result = await departementService.delete(id);
    
    if (result.success) {
      alert("D√©partement supprim√© !");
      loadDepartements();
    } else {
      alert("Erreur : " + result.message);
    }
  };

  const handleCancel = () => {
    setFormData({ nom: "" });
    setShowAddForm(false);
    setEditingDept(null);
  };

  if (loading) {
    return (
      <div className="dept-management">
        <div className="loading">‚è≥ Chargement des d√©partements...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="dept-management">
        <div className="error">
          ‚ùå Erreur : {error}
          <button onClick={loadDepartements} className="retry-btn">
            üîÑ R√©essayer
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="dept-management">
      <div className="header">
        <h2>üìö Gestion des D√©partements</h2>
        <button
          className="add-btn"
          onClick={() => setShowAddForm(!showAddForm)}
        >
          {showAddForm ? "‚ùå Annuler" : "‚ûï Ajouter un d√©partement"}
        </button>
      </div>

      {showAddForm && (
        <div className="form-container">
          <h3>{editingDept ? "‚úèÔ∏è Modifier" : "‚ûï Nouveau"} D√©partement</h3>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="nom">Nom du d√©partement *</label>
              <input
                type="text"
                id="nom"
                value={formData.nom}
                onChange={(e) => setFormData({ nom: e.target.value })}
                placeholder="Ex: Informatique"
                required
              />
            </div>
            <div className="form-actions">
              <button type="submit" className="submit-btn">
                {editingDept ? "üíæ Mettre √† jour" : "‚ûï Cr√©er"}
              </button>
              <button type="button" onClick={handleCancel} className="cancel-btn">
                ‚ùå Annuler
              </button>
            </div>
          </form>
        </div>
      )}

      <div className="dept-list">
        <h3>üìã Liste des D√©partements ({departements.length})</h3>
        
        {departements.length === 0 ? (
          <div className="empty-state">
            <p>Aucun d√©partement trouv√©</p>
            <button onClick={() => setShowAddForm(true)} className="add-first-btn">
              ‚ûï Cr√©er le premier d√©partement
            </button>
          </div>
        ) : (
          <div className="dept-grid">
            {departements.map((dept) => (
              <div key={dept.id} className="dept-card">
                <div className="dept-info">
                  <h4>{dept.nom}</h4>
                  <p className="dept-id">ID: {dept.id}</p>
                  {dept.specialites && dept.specialites.length > 0 && (
                    <p className="dept-specialites">
                      üéì {dept.specialites.length} sp√©cialit√©(s)
                    </p>
                  )}
                  {dept.enseignants && dept.enseignants.length > 0 && (
                    <p className="dept-enseignants">
                      üë®‚Äçüè´ {dept.enseignants.length} enseignant(s)
                    </p>
                  )}
                </div>
                <div className="dept-actions">
                  <button
                    onClick={() => handleEdit(dept)}
                    className="edit-btn"
                    title="Modifier"
                  >
                    ‚úèÔ∏è
                  </button>
                  <button
                    onClick={() => handleDelete(dept.id)}
                    className="delete-btn"
                    title="Supprimer"
                  >
                    üóëÔ∏è
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="refresh-section">
        <button onClick={loadDepartements} className="refresh-btn">
          üîÑ Actualiser
        </button>
      </div>
    </div>
  );
};

export default DepartementManagement;
