import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";

const ChangePasswordPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { changePassword } = useAuth();
  const email = location.state?.email || "";
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [loading, setLoading] = useState(false);

  const [isButtonHovered, setIsButtonHovered] = useState(false);
  const [focusedField, setFocusedField] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setLoading(true);
    
    try {
      const result = await changePassword(email, currentPassword, newPassword);
      if (result.success) {
        setSuccess("Mot de passe chang√© avec succ√®s ! Redirection...");
        
        // Attendre 2 secondes avant de rediriger
        setTimeout(() => {
          const role = result.user?.role;
          if (role === "etudiant") navigate("/student-dashboard");
          else if (role === "enseignant") navigate("/teacher-dashboard");
          else if (role === "directeur_departement") navigate("/director-dashboard");
          else if (role === "administratif") navigate("/admin-dashboard");
          else navigate("/dashboard");
        }, 2000);
      } else {
        setError(result.message);
      }
    } catch (error) {
      setError(
        error.response?.data?.message ||
          "Erreur lors du changement de mot de passe"
      );
    } finally {
      setLoading(false);
    }
  };

  const styles = {
    container: {
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: '#ffffff',
      padding: '20px',
    },
    card: {
      backgroundColor: '#ffffff',
      borderRadius: '16px',
      boxShadow: '0 20px 60px rgba(0, 0, 0, 0.3)',
      padding: '40px',
      width: '100%',
      maxWidth: '500px',
    },
    header: {
      textAlign: 'center',
      marginBottom: '30px',
    },
    iconWrapper: {
      width: '70px',
      height: '70px',
      margin: '0 auto 20px',
      backgroundColor: '#667eea',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    icon: {
      fontSize: '32px',
      color: '#ffffff',
    },
    title: {
      fontSize: '28px',
      fontWeight: '700',
      color: '#1a202c',
      marginBottom: '10px',
    },
    description: {
      fontSize: '14px',
      color: '#718096',
      lineHeight: '1.5',
    },
    alertSuccess: {
      padding: '16px',
      marginBottom: '20px',
      backgroundColor: '#d4edda',
      border: '1px solid #c3e6cb',
      borderRadius: '8px',
      display: 'flex',
      alignItems: 'flex-start',
      gap: '12px',
    },
    alertError: {
      padding: '16px',
      marginBottom: '20px',
      backgroundColor: '#f8d7da',
      border: '1px solid #f5c6cb',
      borderRadius: '8px',
      display: 'flex',
      alignItems: 'flex-start',
      gap: '12px',
    },
    alertIcon: {
      fontSize: '20px',
      flexShrink: 0,
    },
    alertText: {
      fontSize: '14px',
      margin: 0,
    },
    formGroup: {
      marginBottom: '24px',
    },
    label: {
      display: 'block',
      fontSize: '14px',
      fontWeight: '600',
      color: '#2d3748',
      marginBottom: '8px',
    },
    inputWrapper: {
      position: 'relative',
    },
    inputIcon: {
      position: 'absolute',
      left: '14px',
      top: '50%',
      transform: 'translateY(-50%)',
      fontSize: '20px',
      color: '#a0aec0',
      pointerEvents: 'none',
    },
    input: {
      width: '100%',
      padding: '14px 14px 14px 46px',
      fontSize: '15px',
      border: '2px solid #e2e8f0',
      borderRadius: '10px',
      outline: 'none',
      transition: 'all 0.3s ease',
      fontFamily: 'inherit',
      boxSizing: 'border-box',
    },
    inputReadonly: {
      width: '100%',
      padding: '14px 14px 14px 46px',
      fontSize: '15px',
      border: '2px solid #e2e8f0',
      borderRadius: '10px',
      outline: 'none',
      fontFamily: 'inherit',
      boxSizing: 'border-box',
      backgroundColor: '#f7fafc',
      color: '#718096',
      cursor: 'not-allowed',
    },
    inputFocus: {
      borderColor: '#667eea',
      boxShadow: '0 0 0 3px rgba(102, 126, 234, 0.1)',
    },
    button: {
      width: '100%',
      padding: '14px',
      fontSize: '16px',
      fontWeight: '600',
      color: '#ffffff',
      backgroundColor: '#667eea',
      border: 'none',
      borderRadius: '10px',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '8px',
      boxShadow: '0 4px 12px rgba(102, 126, 234, 0.4)',
      marginTop: '10px',
    },
    buttonHover: {
      backgroundColor: '#5568d3',
      transform: 'translateY(-2px)',
      boxShadow: '0 6px 20px rgba(102, 126, 234, 0.5)',
    },
    buttonDisabled: {
      backgroundColor: '#a0aec0',
      cursor: 'not-allowed',
      transform: 'none',
      boxShadow: 'none',
    },
    spinner: {
      display: 'inline-block',
      width: '20px',
      height: '20px',
      border: '3px solid rgba(255, 255, 255, 0.3)',
      borderTopColor: '#ffffff',
      borderRadius: '50%',
      animation: 'spin 0.8s linear infinite',
    },
    passwordHint: {
      fontSize: '12px',
      color: '#718096',
      marginTop: '6px',
      lineHeight: '1.4',
    },
  };

  return (
    <>
      <style>
        {`
          @keyframes spin {
            to { transform: rotate(360deg); }
          }
        `}
      </style>
      <div style={styles.container}>
        <div style={styles.card}>
          <div style={styles.header}>
            <div style={styles.iconWrapper}>
              <span style={styles.icon}>üîí</span>
            </div>
            <h1 style={styles.title}>Changer votre mot de passe</h1>
            <p style={styles.description}>
              Pour votre s√©curit√©, veuillez entrer votre ancien mot de passe et choisir un nouveau mot de passe s√©curis√©.
            </p>
          </div>

          {success && (
            <div style={styles.alertSuccess}>
              <span style={{...styles.alertIcon, color: '#155724'}}>‚úì</span>
              <p style={{...styles.alertText, color: '#155724'}}>{success}</p>
            </div>
          )}

          {error && (
            <div style={styles.alertError}>
              <span style={{...styles.alertIcon, color: '#721c24'}}>‚ö†</span>
              <p style={{...styles.alertText, color: '#721c24'}}>{error}</p>
            </div>
          )}

          <div>
            <div style={styles.formGroup}>
              <label htmlFor="email" style={styles.label}>
                Adresse email
              </label>
              <div style={styles.inputWrapper}>
                <span style={styles.inputIcon}>‚úâ</span>
                <input
                  type="email"
                  id="email"
                  value={email}
                  readOnly
                  style={styles.inputReadonly}
                />
              </div>
            </div>

            <div style={styles.formGroup}>
              <label htmlFor="currentPassword" style={styles.label}>
                Ancien mot de passe
              </label>
              <div style={styles.inputWrapper}>
                <span style={styles.inputIcon}>üîë</span>
                <input
                  type="password"
                  id="currentPassword"
                  value={currentPassword}
                  onChange={(e) => setCurrentPassword(e.target.value)}
                  onFocus={() => setFocusedField('current')}
                  onBlur={() => setFocusedField(null)}
                  placeholder="Votre ancien mot de passe"
                  required
                  disabled={loading}
                  style={{
                    ...styles.input,
                    ...(focusedField === 'current' ? styles.inputFocus : {}),
                    ...(loading ? { opacity: 0.6, cursor: 'not-allowed' } : {}),
                  }}
                />
              </div>
            </div>

            <div style={styles.formGroup}>
              <label htmlFor="newPassword" style={styles.label}>
                Nouveau mot de passe
              </label>
              <div style={styles.inputWrapper}>
                <span style={styles.inputIcon}>üîê</span>
                <input
                  type="password"
                  id="newPassword"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  onFocus={() => setFocusedField('new')}
                  onBlur={() => setFocusedField(null)}
                  placeholder="Votre nouveau mot de passe"
                  required
                  disabled={loading}
                  style={{
                    ...styles.input,
                    ...(focusedField === 'new' ? styles.inputFocus : {}),
                    ...(loading ? { opacity: 0.6, cursor: 'not-allowed' } : {}),
                  }}
                />
              </div>
              <p style={styles.passwordHint}>
                üí° Conseil : Utilisez au moins 8 caract√®res avec des lettres, chiffres et symboles.
              </p>
            </div>

            <button
              onClick={handleSubmit}
              disabled={loading || !currentPassword || !newPassword}
              onMouseEnter={() => setIsButtonHovered(true)}
              onMouseLeave={() => setIsButtonHovered(false)}
              style={{
                ...styles.button,
                ...(isButtonHovered && !loading && currentPassword && newPassword ? styles.buttonHover : {}),
                ...(loading || !currentPassword || !newPassword ? styles.buttonDisabled : {}),
              }}
            >
              {loading ? (
                <>
                  <span style={styles.spinner}></span>
                  <span>Changement en cours...</span>
                </>
              ) : (
                <span>Changer le mot de passe</span>
              )}
            </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default ChangePasswordPage;
