import React, { useState, useEffect } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import firebaseAuthService from '../services/firebaseAuthService';
import './LoginPage.css';

function ResetPasswordPage() {
  const [searchParams] = useSearchParams();
  const [email, setEmail] = useState('');
  const [token, setToken] = useState('');
  const [oobCode, setOobCode] = useState('');
  const [mode, setMode] = useState(''); // 'firebase' or 'local'
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [verifying, setVerifying] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if it's a Firebase reset link or local token link
    const modeParam = searchParams.get('mode');
    const oobCodeParam = searchParams.get('oobCode');
    const emailParam = searchParams.get('email');
    const tokenParam = searchParams.get('token');
    
    if (modeParam === 'resetPassword' && oobCodeParam) {
      // Firebase reset link
      setMode('firebase');
      setOobCode(oobCodeParam);
      verifyFirebaseCode(oobCodeParam);
    } else if (emailParam && tokenParam) {
      // Local token reset link
      setMode('local');
      setEmail(emailParam);
      setToken(tokenParam);
      setVerifying(false);
    } else {
      setError('Lien invalide. Veuillez demander un nouveau lien de rÃ©initialisation.');
      setVerifying(false);
    }
  }, [searchParams]);

  const verifyFirebaseCode = async (code) => {
    try {
      const userEmail = await firebaseAuthService.verifyPasswordResetCode(code);
      setEmail(userEmail);
      setVerifying(false);
      console.log('âœ… Firebase code verified for:', userEmail);
    } catch (err) {
      console.error('âŒ Error verifying Firebase code:', err);
      setError('Le lien de rÃ©initialisation est invalide ou a expirÃ©. Veuillez demander un nouveau lien.');
      setVerifying(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setMessage('');

    if (newPassword !== confirmPassword) {
      setError('Les mots de passe ne correspondent pas');
      return;
    }

    if (newPassword.length < 8) {
      setError('Le mot de passe doit contenir au moins 8 caractÃ¨res');
      return;
    }

    setLoading(true);

    try {
      if (mode === 'firebase') {
        // Use Firebase to reset password
        await firebaseAuthService.confirmPasswordReset(oobCode, newPassword);
        setMessage('âœ… Mot de passe rÃ©initialisÃ© avec succÃ¨s via Firebase !');
      } else {
        // Use local token to reset password
        await firebaseAuthService.resetPasswordWithToken(email, token, newPassword);
        setMessage('âœ… Mot de passe rÃ©initialisÃ© avec succÃ¨s !');
      }
      
      setLoading(false);
      
      // Auto-redirect after 3 seconds
      setTimeout(() => {
        navigate('/login');
      }, 3000);
    } catch (err) {
      console.error('Reset password error:', err);
      setError(
        err.response?.data?.message || 
        err.message ||
        'Une erreur est survenue. Le lien est peut-Ãªtre expirÃ©.'
      );
      setLoading(false);
    }
  };

  if (verifying) {
    return (
      <div className="login-container">
        <div className="login-box">
          <h1>VÃ©rification...</h1>
          <p style={{ textAlign: 'center', color: '#666' }}>
            VÃ©rification du lien de rÃ©initialisation en cours...
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="login-container">
      <div className="login-box">
        <h1>RÃ©initialiser le mot de passe</h1>
        <p style={{ marginBottom: '20px', color: '#666' }}>
          {mode === 'firebase' ? 'ðŸ”¥ RÃ©initialisation via Firebase' : 'Entrez votre nouveau mot de passe'}
        </p>

        {message && (
          <div style={{
            padding: '15px',
            marginBottom: '15px',
            backgroundColor: '#d4edda',
            color: '#155724',
            borderRadius: '4px',
            border: '1px solid #c3e6cb',
            textAlign: 'center'
          }}>
            <p style={{ margin: '0 0 10px 0', fontWeight: 'bold' }}>{message}</p>
            <p style={{ margin: '0 0 15px 0', fontSize: '14px' }}>
              Redirection automatique dans 3 secondes...
            </p>
            <button
              onClick={() => navigate('/login')}
              style={{
                padding: '10px 20px',
                backgroundColor: '#28a745',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                fontSize: '14px',
                fontWeight: 'bold'
              }}
            >
              Continuer vers la connexion â†’
            </button>
          </div>
        )}

        {error && (
          <div style={{
            padding: '10px',
            marginBottom: '15px',
            backgroundColor: '#f8d7da',
            color: '#721c24',
            borderRadius: '4px',
            border: '1px solid #f5c6cb'
          }}>
            {error}
          </div>
        )}

        {!message && (
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                value={email}
                disabled
                style={{ backgroundColor: '#f5f5f5' }}
              />
            </div>

            <div className="form-group">
              <label>Nouveau mot de passe</label>
              <input
                type="password"
                value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              placeholder="Minimum 8 caractÃ¨res"
              required
              disabled={loading || (!token && !oobCode)}
              minLength={8}
            />
          </div>

          <div className="form-group">
            <label>Confirmer le mot de passe</label>
            <input
              type="password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              placeholder="Confirmez votre mot de passe"
              required
              disabled={loading || (!token && !oobCode)}
              minLength={8}
            />
          </div>

          <button 
            type="submit" 
            className="login-button"
            disabled={loading || (!token && !oobCode)}
          >
            {loading ? 'RÃ©initialisation...' : 'RÃ©initialiser le mot de passe'}
          </button>
        </form>
        )}

        {!message && (
          <div style={{ marginTop: '20px', textAlign: 'center' }}>
            <button
              onClick={() => navigate('/login')}
              style={{
                background: 'none',
                border: 'none',
                color: '#007bff',
                cursor: 'pointer',
                textDecoration: 'underline'
              }}
            >
              Retour Ã  la connexion
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default ResetPasswordPage;
