      />

      <EditClasseModal 
        isOpen={showEditClasseModal}
        onClose={() => {
          setShowEditClasseModal(false);
          setSelectedClasse(null);
        }}
        onClasseUpdated={() => {
          loadClasses();
          loadDashboardStats();
        }}
        classe={selectedClasse}
      />
    </div>
  );
};

export default AdminDashboard;
